const OpenAI = require('openai');
const client = new OpenAI({
  apiKey: 'YOUR_NUSA_KEY',
  baseURL: 'https://api.neosantara.xyz/v1'
});

const response = await client.images.generate({
  model: 'flux-schnell',
  prompt: 'Pemandangan alam Indonesia yang indah di pagi hari',
  n: 1,
  size: '1024x1024',
});

const OpenAI = require('openai');
const client = new OpenAI({
  apiKey: 'YOUR_NUSA_KEY',
  baseURL: 'https://api.neosantara.xyz/v1'
});

const response = await client.chat.completions.create({
  model: 'nusantara-pro',
  messages: [{ role: 'user', content: 'Halo, apa kabar?' }],
});

import OpenAI from 'openai';

const client = new OpenAI({
  apiKey: 'YOUR_NUSA_KEY',
  baseURL: 'https://api.neosantara.xyz/v1'
});

const response = await client.chat.completions.create({
  model: 'nusantara-base',
  messages: [{ role: 'user', content: 'Halo, apa kabar?' }],
});
console.log(response.choices[0].message.content);

const axios = require('axios');

const response = await axios.post('https://api.neosantara.xyz/v1/responses', {
  model: 'nusantara-pro',
  input: 'Jelaskan apa itu Nusantara AI',
  instructions: 'Gunakan bahasa yang santai.'
}, {
  headers: { 'Authorization': `Bearer ${process.env.NUSA_KEY}` }
});

import OpenAI from 'openai';

const client = new OpenAI({
  apiKey: 'YOUR_NUSA_KEY',
  baseURL: 'https://api.neosantara.xyz/v1'
});

const response = await client.responses.create({
  model: 'nusantara-base',
  input: 'Halo, apa kabar?',
  instructions: 'Jawab dengan gaya santai.',
  store: true
});
console.log(response.output_text);

import Anthropic from '@anthropic-ai/sdk';

const client = new Anthropic({
  apiKey: 'YOUR_NUSA_KEY',
  baseURL: 'https://api.neosantara.xyz/anthropic'
});

const message = await client.messages.create({
  model: 'nusantara-base',
  max_tokens: 1024,
  messages: [{ role: 'user', content: 'Halo, apa kabar?' }],
});
console.log(message.content[0].text);
